import React from 'react';
import { Button, Image, Popconfirm, Table, TableColumnsType } from 'antd';
import { DataTableProps, DataType } from 'models';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';
import { filter } from 'lodash';

const DataTable = (props: DataTableProps) => {
  const defaultColumns = [
    {
      title: 'ID',
      dataIndex: 'key',
      width: '150px',
    },
    {
      title: 'Name',
      dataIndex: 'name',
    },
    {
      title: 'Action',
      dataIndex: 'action',
      key: 'x',
      width: '110px',
      render: (_: any, record: DataType) =>
        props.dataSource && props.dataSource.length > 0 ?
          <>
            <Button
              style={{ marginRight: '10px' }}
              shape='circle'
              icon={<EditOutlined />}
              onClick={() => props.onEditRecord(record.key)}
            />
            <Popconfirm
              title="Sure to delete?"
              onConfirm={() => props.onDeleteRecord(record.key)}>
              <Button
                shape='circle'
                icon={<DeleteOutlined />}
              />
            </Popconfirm>

          </> : null
    },
  ];

  const newColumns: TableColumnsType<DataType> =
    props.customColumns ?
      [
        {
          title: 'ID',
          dataIndex: 'key',
          width: '150px',
        },
        (props.isShowImage ?
          {
            title: 'Image',
            dataIndex: 'imgUrl',
            width: '120px',
            render: (_: any, rd: DataType) =>
              <Image
                width={88}
                src={rd.imgUrl}
                fallback="data:image/png;base64,"
              />
          } : {}),
        {
          title: 'Name',
          dataIndex: 'name',
        },
        ...props.customColumns,
        {
          title: 'Action',
          dataIndex: 'action',
          key: 'x',
          width: '110px',
          render: (_: any, record: DataType) =>
            props.dataSource && props.dataSource.length > 0 ?
              <>
                <Button
                  style={{ marginRight: '10px' }}
                  shape='circle'
                  icon={<EditOutlined />}
                  onClick={() => props.onEditRecord(record.key)}
                />
                <Popconfirm
                  title="Sure to delete?"
                  onConfirm={() => props.onDeleteRecord(record.key)}>
                  <Button
                    shape='circle'
                    icon={<DeleteOutlined />}
                  />
                </Popconfirm>

              </> : null
        }] : [...defaultColumns];

  return (
    <Table
      bordered
      columns={filter(newColumns, col => Object.keys(col).length !== 0)}
      {...props}
    />
  );
};

export default DataTable;
